from django.contrib import messagesfrom django.views.generic import ListViewfrom django.views.generic.edit import FormViewfrom django.shortcuts import redirectfrom .form import TotalFormfrom .models import Botfrom .tasks import create_botclass GenerateBotView(FormView):    template_name = 'generate_bots.html'    form_class = TotalForm    success_url = '/bots-list/'    def form_valid(self, form):        total_by_user = self.request.session.get('total_by_user', 0)        self.request.session['total_by_user'] = total_by_user + form.cleaned_data.get('total')        create_bot.delay(form.cleaned_data.get('total'))        messages.success(self.request, f'Bot generation started. {self.request.session.get("total_by_user")}')        return redirect("bots_list")class BotsListView(ListView):    model = Bot    template_name = 'bot_list.html'